import React, {Component} from "react";
import SideNav, {Toggle, Nav, NavItem, NavIcon, NavText} from '@trendmicro/react-sidenav';
import {
    MDBNavbar,
    MDBNavbarBrand,
    MDBNavbarNav,
    MDBNavbarToggler,
    MDBCollapse,
    MDBNavItem,
    MDBFooter,
    MDBNavLink, MDBRow, MDBCol, MDBJumbotron, MDBCardBody, MDBCardTitle, MDBCardText, MDBBtn, MDBIcon, MDBContainer
} from "mdbreact";
import {ReactComponent as Logo} from "./assets/logo.svg";
import {BrowserRouter as Router} from "react-router-dom";
import Routes from "./Routes";
import SectionContainer from "./components/sectionContainer";
import Route from "react-router-dom/es/Route";
import AddonsNavPage from "./pages/AddonsNavPage";
import AdvancedNavPage from "./pages/AdvancedNavPage";
import ComponentsNavPage from "./pages/ComponentsNavPage";
import CSSPage from "./pages/CSSNavPage"
import FormsNavPage from "./pages/FormsNavPage";
import HomePage from "./pages/HomePage";
import ModalsNavPage from "./pages/ModalsNavPage";
import NavigationNavPage from "./pages/NavigationNavPage";
import TablesNavPage from "./pages/TablesNavPage";
import ClickOutHandler from 'react-onclickout';

class App extends Component {
    state = {
        collapseID: ""
    };
    toggleCollapse = collapseID => () =>
        this.setState(prevState => ({
            collapseID: prevState.collapseID !== collapseID ? collapseID : ""
        }));

    closeCollapse = collapseID => () => {
        window.scrollTo(0, 0);
        this.state.collapseID === collapseID && this.setState({collapseID: ""});
    };

    render() {
        const overlay = (
            <div
                id="sidenav-overlay"
                style={{backgroundColor: "transparent"}}
                onClick={this.toggleCollapse("mainNavbarCollapse")}
            />
        );

        const {collapseID} = this.state;
        const background = "https://upload.wikimedia.org/wikipedia/commons/4/43/Conmaicne_Rein_700AD.jpg";

        return (
            <ClickOutHandler onClickOut={this.close}>
                <Router>
                    <Route render={({location, history}) => (
                        <React.Fragment>
                            <SideNav
                                onSelect={(selected) => {
                                    const to = '/' + selected;
                                    if (location.pathname !== to) {
                                        history.push(to);
                                    }
                                }}>
                                <SideNav.Toggle/>
                                <SideNav.Nav defaultSelected="home">
                                    <NavItem eventKey="home">
                                        <NavIcon>
                                            <i className="fa fa-fw fa-home" style={{fontSize: '1.75em'}}/>
                                        </NavIcon>
                                        <NavText>
                                            Home
                                        </NavText>
                                    </NavItem>
                                    <NavItem eventKey="css">
                                        <NavIcon>
                                            <i className="fa fa-fw fa-css-page" style={{fontSize: '1.75em'}}/>
                                        </NavIcon>
                                        <NavText>
                                            CSS
                                        </NavText>
                                    </NavItem>
                                    <NavItem eventKey="components">
                                        <NavIcon>
                                            <i className="fa fa-fw fa-components-page" style={{fontSize: '1.75em'}}/>
                                        </NavIcon>
                                        <NavText>
                                            Components
                                        </NavText>
                                    </NavItem>
                                    <NavItem eventKey="advanced">
                                        <NavIcon>
                                            <i className="fa fa-fw fa-advanced-page" style={{fontSize: '1.75em'}}/>
                                        </NavIcon>
                                        <NavText>
                                            Advanced
                                        </NavText>
                                    </NavItem>
                                    <NavItem eventKey="navigation">
                                        <NavIcon>
                                            <i className="fa fa-fw fa-navigation-page" style={{fontSize: '1.75em'}}/>
                                        </NavIcon>
                                        <NavText>
                                            Navigation
                                        </NavText>
                                    </NavItem>
                                    <NavItem eventKey="forms">
                                        <NavIcon>
                                            <i className="fa fa-fw fa-forms-page" style={{fontSize: '1.75em'}}/>
                                        </NavIcon>
                                        <NavText>
                                            Forms
                                        </NavText>
                                    </NavItem>
                                    <NavItem eventKey="tables">
                                        <NavIcon>
                                            <i className="fa fa-fw fa-table-page" style={{fontSize: '1.75em'}}/>
                                        </NavIcon>
                                        <NavText>
                                            Tables
                                        </NavText>
                                    </NavItem>
                                    <NavItem eventKey="modals">
                                        <NavIcon>
                                            <i className="fa fa-fw fa-modals-page" style={{fontSize: '1.75em'}}/>
                                        </NavIcon>
                                        <NavText>
                                            Modals
                                        </NavText>
                                    </NavItem>
                                    <NavItem eventKey="addons">
                                        <NavIcon>
                                            <i className="fa fa-fw fa-addons-page" style={{fontSize: '1.75em'}}/>
                                        </NavIcon>
                                        <NavText>
                                            Addons
                                        </NavText>
                                    </NavItem>
                                </SideNav.Nav>
                            </SideNav>
                            < main>
                                <Route path="/" exact component={props => <HomePage/>}/>
                                <Route path="/css" exact component={props => <CSSPage/>}/>
                                <Route path="/components" component={props => <ComponentsNavPage/>}/>
                                <Route path="/advanced" component={props => <AdvancedNavPage/>}/>
                                <Route path="/navigation" component={props => <NavigationNavPage/>}/>
                                <Route path="/forms" component={props => <FormsNavPage/>}/>
                                <Route path="/tables" component={props => <TablesNavPage/>}/>
                                <Route path="/modals" component={props => <ModalsNavPage/>}/>
                                <Route path="/addons" component={props => <AddonsNavPage/>}/>
                            </main>
                        </React.Fragment>
                    )}
                    />
                </Router>
            </ClickOutHandler>
        );
    }
}

export default App;
