import React, { Component } from "react";

export default class FancyBorder extends Component {
    render() {
        const fancyBorder = this.props.map(c => (
                <div className={'FancyBorder FancyBorder-' + c.color}>
                    {c.children}
                </div>
        ));
        return (
            fancyBorder
        );
    }
}
